package date;import java.text.SimpleDateFormat;import java.util.Date;public class DateUtils {    private static final String DATE_FORMAT = "yyyy-MM-dd HH:mm:ss";    public static final String timestampFormat(long timestamp){        Date date = new Date(timestamp);        return new SimpleDateFormat(DATE_FORMAT).format(date);    }    public static final String now(){        return new SimpleDateFormat(DATE_FORMAT).format(new Date());    }}